.table {
    display: table;
    width: 100%;
    max-width:100%;
}
.table-row-group {
    display: table-row-group
}
.table-row {
    display: table-row;
}
.table-cell {
    display: table-cell;
    text-align: right;
}

.top-plugin {
    margin-bottom: 20px;
}

.plugin {
    margin-bottom: 20px;
}
.plugin.table-row-group .table-row:last-child .table-cell {
    padding-bottom: 20px;
}

.underline {
    text-decoration: underline
}
.bold {
    font-weight: bold;
}
.sort {
    font-weight: bold;
    color: white;
}
.sortable {
    cursor: pointer;
}
.text-right {
    text-align: right;
}
.text-left {
    text-align: left;
}
.sidebar .table-cell:not(.text-left) {
    padding-left: 10px;
}

/* Theme */

.title {
    font-weight: bold;
    color: white;
}
.highlight {
    font-weight: bold;
    color: #5D4062;
}
.ok, .status, .process {
    color: #3E7B04;
    /*font-weight: bold;*/
}
.ok_log {
    background-color: #3E7B04;
    color: white;
    /*font-weight: bold;*/
}
.max {
    color: #3E7B04;
    font-weight: bold;
}
.careful {
    color: #295183;
    font-weight: bold;
}
.careful_log {
    background-color: #295183;
    color: white;
    font-weight: bold;
}
.warning, .nice {
    color: #5D4062;
    font-weight: bold;
}
.warning_log {
    background-color: #5D4062;
    color: white;
    font-weight: bold;
}
.critical {
    color: #A30000;
    font-weight: bold;
}
.critical_log {
    background-color: #A30000;
    color: white;
    font-weight: bold;
}

