    background-color: #3E7B04;
}
#quicklook .progress-bar-careful {
    background-color: #295183;
}
#quicklook .progress-bar-warning {
    background-color: #5D4062;
}
#quicklook .progress-bar-critical {
    background-color: #A30000;
}
